mapper.select:
select user.uid, user.username, u.group_name, user.permission
from user
         left join user_group u on user.group_id = u.gid;

select user.uid, user.username, u.group_name, user.permission
from (
         select *
         from user
         where user.uid = #{uid}
     ) user
         left join user_group u on user.group_id = u.gid;

select user.uid, user.username, u.group_name, user.permission
from (
         select *
         from user
         where user.uid = #{uid} and user.password = #{password}) user
         left join user_group u on user.group_id = u.gid;

{
    call userNum(
        #{adminNum, mode = OUT, jdbcType = INTEGER},
        #{counsellorNum, mode = OUT, jdbcType = INTEGER},
        #{assistantNum, mode = OUT, jdbcType = INTEGER}
    )
}
